// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    // Add Mouse Operation
    vizcore3dx.MouseControl = vizcore3dx.MouseControl | VIZCore3DX.NET.MouseControls.Move;
    vizcore3dx.MouseControl = vizcore3dx.MouseControl | VIZCore3DX.NET.MouseControls.Wheel;

    // Remove Mouse Operation
    vizcore3dx.MouseControl = vizcore3dx.MouseControl & ~VIZCore3DX.NET.MouseControls.Move;
    vizcore3dx.MouseControl = vizcore3dx.MouseControl & ~VIZCore3DX.NET.MouseControls.Wheel;

    // Has Flag
    if (vizcore3dx.MouseControl.HasFlag(VIZCore3DX.NET.MouseControls.Move) == true)
        vizcore3dx.MouseControl = vizcore3dx.MouseControl & ~VIZCore3DX.NET.MouseControls.Move;
    else
        vizcore3dx.MouseControl = vizcore3dx.MouseControl | VIZCore3DX.NET.MouseControls.Move;
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    //===================================
    // Environment
    //===================================
    string assemblyPath = VIZCore3DX.NET.ModuleInitializer.GetAssemblyLocation();
    string entryAssemblyPath = VIZCore3DX.NET.ModuleInitializer.GetEntryAssemblyLocation();

    string assemblyVersion = VIZCore3DX.NET.ModuleInitializer.GetLibraryVersion();
    string interopVersion = VIZCore3DX.NET.ModuleInitializer.GetInteropLibraryVersion();
    bool compareVersion = VIZCore3DX.NET.ModuleInitializer.CompareVersion();

    bool existLibrary = VIZCore3DX.NET.ModuleInitializer.ExistLibrary();

    // Initialize VIZCore3DX.NET
    // Case 1. : Extract Library by Condition
    //if(existLibrary == false || compareVersion == false)
    //    VIZCore3DX.NET.ModuleInitializer.Run();
    // Case 2. : Extract Library Always
    VIZCore3DX.NET.ModuleInitializer.Run();

    vizcore3dx = new VIZCore3DX.NET.VIZCore3DXControl();

    vizcore3dx.Dock = DockStyle.Fill;
    panelView.Controls.Add(vizcore3dx);

    // Event
    vizcore3dx.OnInitializedVIZCore3D += VIZCore3DX_OnInitializedVIZCore3D;
}

private void VIZCore3DX_OnInitializedVIZCore3D(object sender, EventArgs e)
{
    VIZCore3DX.NET.Data.LicenseResults result =
        vizcore3dx.License.LicenseServer("0.0.0.0", 8901);

    if (result != Data.LicenseResults.SUCCESS)
    {
        MessageBox.Show(result.ToString()
        , "VIZCore3DX.NET"
        , MessageBoxButtons.OK
        , MessageBoxIcon.Error
        );

        return;
    }

    // Init. Some Options...
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    List<VIZCore3DX.NET.Data.Node> items =
        vizcore3dx.Object3D.FromFilter(
            VIZCore3DX.NET.Data.Object3dFilter.SELECTED_TOP
            );

    if (items.Count == 0) return;

    // 부재의 표면점 조회
    VIZCore3DX.NET.Data.Vertex3D surfacePt = vizcore3dx.Object3D.GetSurfaceVertexClosestToModelCenter(
        new List<int>() { items[0].Index });

    // 화면 갱신 차단
    vizcore3dx.BeginUpdate();

    for (int i = 0; i < int.MaxValue; i++)
    {
        // 노트 생성
        vizcore3dx.Review.Note.AddNoteSurface(
            string.Format("Note No. {0}", i + 1)
            , new VIZCore3DX.NET.Data.Vertex3D(
                surfacePt.X + 2000.0f
                , surfacePt.Y
                , surfacePt.Z + 2000.0f
                )
            , surfacePt
            );
    }

    // 화면 갱신 차단 해제
    vizcore3dx.EndUpdate();
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

