        new List<int>() { items[0].Index });

    // 화면 갱신 차단
    vizcore3dx.BeginUpdate();

    for (int i = 0; i < int.MaxValue; i++)
    {
        // 노트 생성
        vizcore3dx.Review.Note.AddNoteSurface(
