// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example(List<VIZCore3DX.NET.Data.Node> items)
{
    // 정보패널(Information Panel)에 추가하고자 하는 외부 컨트롤
    System.Windows.Forms.UserControl customCtrl 
        = new System.Windows.Forms.UserControl();

    ShowInformationPanel(customCtrl);
}

private void ShowInformationPanel(UserControl ctrl)
{
    ctrl.Dock = DockStyle.Fill;

    // 기존 컨트롤이 있는 경우, 제거
    if (vizcore3dx.InformationPanel.Controls.Count != 0)
        vizcore3dx.InformationPanel.Controls.Clear();

    // 새로운 컨트롤 삽입
    vizcore3dx.InformationPanel.Controls.Add(ctrl);

    // 조회
    vizcore3dx.InformationPanel.Visible = true;
}

private void HideInformationPanel()
{
    // 숨기기
    vizcore3dx.InformationPanel.Visible = false;
}


