private void VIZCore3DX_OnInitializedVIZCore3D(object sender, EventArgs e)
{
    // ================================================================
    // Example
    // ================================================================
    //vizcore3dx.License.LicenseFile("C:\\Temp\\VIZCore3DX.NET.lic");                         // 라이선스 파일
    //vizcore3dx.License.LicenseServer("127.0.0.1", 8901);                                   // 라이선스 서버 - 제품 자동 선택
    //vizcore3dx.License.LicenseServer("127.0.0.1", 8901, Data.Products.AUTO);               // 라이선스 서버 - 제품 자동 선택
    //vizcore3dx.License.LicenseServer("127.0.0.1", 8901, Data.Products.VIZZARD_MANAGER);    // 라이선스 서버 - 지정된 제품으로 인증
    //vizcore3dx.License.LicenseServer("127.0.0.1", 8901, Data.Products.VIZZARD_STANDARD);   // 라이선스 서버 - 지정된 제품으로 인증
    //vizcore3dx.License.LicenseServer("127.0.0.1", 8901, Data.Products.VIZCore3D_MANAGER);  // 라이선스 서버 - 지정된 제품으로 인증
    //vizcore3dx.License.LicenseServer("127.0.0.1", 8901, Data.Products.VIZCore3D_STANDARD); // 라이선스 서버 - 지정된 제품으로 인증


    // ================================================================
    // TEST
    // ================================================================
    VIZCore3DX.NET.Data.LicenseResults result = vizcore3dx.License.LicenseFile("C:\\License\\VIZCore3DX.NET.lic");
    //VIZCore3DX.NET.Data.LicenseResults result = vizcore3dx.License.LicenseServer("127.0.0.1", 8901);
    //VIZCore3DX.NET.Data.LicenseResults result = vizcore3dx.License.LicenseServer("192.168.0.215", 8901);
    if (result != VIZCore3DX.NET.Data.LicenseResults.SUCCESS)
    {
        MessageBox.Show(string.Format("LICENSE CODE : {0}", result.ToString()), "VIZCore3DX.NET", MessageBoxButtons.OK, MessageBoxIcon.Error);
        return;
    }

    // Init. VIZCore3DX.NET
    // Call Setting Function.
    // Call Init. Event
}


