// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    // Add Event
    vizcore3dx.OnToolbarItemClicked += VIZCore3DX_OnToolbarItemClicked;
}

private void VIZCore3DX_OnToolbarItemClicked(object sender, Event.ToolbarItemClickedEventArgs e)
{
    if(e.Item == VIZCore3DX.NET.Data.ToolbarItems.MAIN_SAVE_VIZ 
        || e.Item == VIZCore3DX.NET.Data.ToolbarItems.MAIN_SAVEAS_VIZ)
    {
        // Disable Menu :: Save
        e.Cancel = true;
    }
    else
    {
        e.Cancel = false;
    }
}


